"use strict";

const PluginArray = require("jsdom/lib/jsdom/living/generated/PluginArray");
const MimeTypeArray = require("jsdom/lib/jsdom/living/generated/MimeTypeArray");

exports.implementation = class NavigatorPluginsImpl {
  get plugins() {
    return PluginArray.create(this._globalObject);
  }

  get mimeTypes() {
    return MimeTypeArray.create(this._globalObject);
  }

  javaEnabled() {
    return false;
  }
};
