"use strict";

const CharacterDataImpl = require("jsdom/lib/jsdom/living/nodes/CharacterData-impl").implementation;

const NODE_TYPE = require("jsdom/lib/jsdom/living/node-type");

class CommentImpl extends CharacterDataImpl {
  constructor(globalObject, args, privateData) {
    super(globalObject, args, {
      data: args[0],
      ...privateData
    });

    this.nodeType = NODE_TYPE.COMMENT_NODE;
  }
}

module.exports = {
  implementation: CommentImpl
};
